CREATE TABLE IF NOT EXISTS `BorrowRecords` ( `id` int NOT NULL AUTO_INCREMENT PRIMARY KEY, `book_id` int NOT NULL, `user_id` int NOT NULL, `borrow_time` date NOT NULL, `return_time` date, `borrow_status` ENUM('BORROWED', 'RETURNED', 'OVERDUE') NOT NULL, CONSTRAINT  FOREIGN KEY (`user_id`) REFERENCES `Users` (`id`) ON DELETE RESTRICT ON UPDATE CASCADE, CONSTRAINT  FOREIGN KEY (`book_id`) REFERENCES `BookManagers` (`id`) ON DELETE RESTRICT ON UPDATE CASCADE )